DROP TABLE IF EXISTS Messages;
DROP TABLE IF EXISTS Chats;
DROP TABLE IF EXISTS Posts;
DROP TABLE IF EXISTS Users;

-- Définition des Tables
--    Table des utilisateurs
CREATE TABLE IF NOT EXISTS Users (
   id          INT(8) UNSIGNED   NOT NULL AUTO_INCREMENT,   -- identifant unique
   auto        TINYINT(2)                 DEFAULT 0,        -- 0 -> client 1 -> owner 2 -> admin
   login       VARCHAR(50)       NOT NULL DEFAULT '',       -- login
   firstname   VARCHAR(256)      NOT NULL DEFAULT '',
   lastname    VARCHAR(256)      NOT NULL DEFAULT '',
   mail        VARCHAR(256)      NOT NULL DEFAULT '',       -- adresse mail unique
   pwd         VARCHAR(256)      NOT NULL DEFAULT '',       -- mot de passe crypté
   phone       CHAR(10)          NOT NULL DEFAULT '',
   PRIMARY KEY (id)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

--    Table des annonces
CREATE TABLE IF NOT EXISTS Posts (
   id          INT(8) UNSIGNED   NOT NULL AUTO_INCREMENT,   -- identifant unique
   owner_id    INT(8) UNSIGNED   NOT NULL,                  -- identifant de l'utilisateur propriétaire
   title       VARCHAR(256)      NOT NULL DEFAULT '',
   descr       TEXT              NOT NULL,                  -- description de l'annonce
   img         VARCHAR(256)      NOT NULL DEFAULT '',
   nb_rooms    INT(8) UNSIGNED   NOT NULL DEFAULT 1,        -- nombre de pièces
   area        INT(8) UNSIGNED   NOT NULL DEFAULT 0,        -- superficie en mètres carré
   type        TINYINT(2)        NOT NULL DEFAULT 0,        -- 0 -> studio 1 -> appart 2 -> maison
   price       DECIMAL(8,2)      NOT NULL DEFAULT 0,
   adr         VARCHAR(256)      NOT NULL DEFAULT '',       -- addresse postale
   city        VARCHAR(256)      NOT NULL DEFAULT '',
   PRIMARY KEY (id),
   FOREIGN KEY (owner_id) REFERENCES Users (id)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

--    Table des conversations
CREATE TABLE IF NOT EXISTS Chats (
   id          INT(8) UNSIGNED NOT NULL AUTO_INCREMENT,
   user1_id    INT(8) UNSIGNED NOT NULL,
   user2_id    INT(8) UNSIGNED NOT NULL,
   PRIMARY KEY (id),
   FOREIGN KEY (user1_id) REFERENCES Users (id),
   FOREIGN KEY (user2_id) REFERENCES Users (id)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

--    Table des messages
CREATE TABLE IF NOT EXISTS Messages (
   id       INT(8) UNSIGNED NOT NULL AUTO_INCREMENT,
   chat_id  INT(8) UNSIGNED NOT NULL,
   user_id  INT(8) UNSIGNED NOT NULL,
   content  TEXT            NOT NULL,
   datime   DATETIME,              -- date et heure de création
   PRIMARY KEY (id),
   FOREIGN KEY (chat_id) REFERENCES Chats (id),
   FOREIGN KEY (user_id) REFERENCES Users (id)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;



